function map() {
    let state = this.Policy.State
    let vin = this.Vehicle.VIN
    let coverage = this.Coverage.Coverage
    var result = {
        "key": { "state": state, "vin": vin , "coverage": coverage},
        "value": {
            "chunkId": this.chunkId,
            "carrierId": this.Policy.CarrierID
        }
    }
    emit(
        result.key, 1,
    )
}
